import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import { GoogleLogin, GoogleLogout } from 'react-google-login';

const CLIENT_ID = '608950634863-oocf4589motggau92gukloto2l01fgha.apps.googleusercontent.com';

console.log(CLIENT_ID);


const GoogleBtn = () => {
    const {token, setToken} = useState();
    const {loggedIn, setLoggedIn} = useState(false);

    const login = (res) => {
    
        if(res.accessToken){
            setLoggedIn(!loggedIn)
            setToken(res.accessToken)
        }
    };

    const logout = (res) => {
        setLoggedIn(!loggedIn);
        setToken('')
    }

    const handleLogInFail = () => {
        window.alert('Failed to log in')
    }

    const handleLogoutFail = () => {
        window.alert('Failed to log out')
    }

    return (
        <div>
            {loggedIn ? 
                <GoogleLogout
                clientId = {CLIENT_ID}
                buttonText = 'Logout'
                onLogoutSuccess = {logout}
                onFailure = {handleLogoutFail}
                />
                : 
                <GoogleLogin 
                clientId = {CLIENT_ID}
                buttonText = 'Sign in with Google'
                onLogoutSuccess = {login}
                onFailure = {handleLogInFail}/>
            }
            {
                token ?
                <h3>Your Access Token: <br/>
                {token}</h3> :
                null
            }
        </div>
    )
}

export default GoogleBtn;
